import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { fetchWithAuth } from '../api';
import decodeJwt from '../utils/decodeJwt';

export default function Blogs() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState('All');
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminSaving, setAdminSaving] = useState(false);
  const [adminForm, setAdminForm] = useState({
    title: '',
    excerpt: '',
    category: 'General',
    image: '',
    content: ''
  });

  useEffect(() => {
    fetchBlogs();
  }, []);

  useEffect(() => {
    const t = typeof window !== 'undefined' ? localStorage.getItem('token') : null;
    if (t) {
      const p = decodeJwt(t);
      setIsAdmin(!!(p && (p.role === 'admin' || p.role === 'super_admin')));
    }
  }, []);

  const fetchBlogs = async () => {
    try {
      const res = await fetch('/api/blogs');
      const data = await res.json();
      setBlogs(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: 'Failed to load blogs', type: 'error' }
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (e) => {
    e.preventDefault();
    setAdminSaving(true);
    try {
      const res = await fetchWithAuth('/api/blogs', {
        method: 'POST',
        body: JSON.stringify(adminForm)
      });
      const body = await res.json();
      if (!res.ok) throw body;
      setAdminForm({
        title: '',
        excerpt: '',
        category: 'General',
        image: '',
        content: ''
      });
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: 'Blog created', type: 'success' }
      }));
      fetchBlogs();
    } catch (err) {
      console.error(err);
      const msg = (err && err.message) || 'Create failed';
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: msg, type: 'error' }
      }));
    } finally {
      setAdminSaving(false);
    }
  };

  const handleDeleteBlog = async (id) => {
    if (!confirm('Delete this blog?')) return;
    try {
      const res = await fetchWithAuth(`/api/blogs/${id}`, { method: 'DELETE' });
      const body = await res.json();
      if (!res.ok) throw body;
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: 'Blog deleted', type: 'success' }
      }));
      fetchBlogs();
    } catch (err) {
      console.error(err);
      const msg = (err && err.message) || 'Delete failed';
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: msg, type: 'error' }
      }));
    }
  };

  const categories = ['All', ...new Set(blogs.map(b => b.category))];
  const filteredBlogs = category === 'All' ? blogs : blogs.filter(b => b.category === category);
  const totalCategories = new Set(blogs.map(b => b.category)).size;
  const latestBlogDate = blogs.reduce((latest, b) => {
    const d = b?.createdAt ? new Date(b.createdAt) : null;
    if (!d || Number.isNaN(d.getTime())) return latest;
    return latest && latest > d ? latest : d;
  }, null);

  if (loading) return <div className="loading">Loading blogs...</div>;

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Travel Blogs</h1>
        <p>Tips, stories, and inspiration for your next journey</p>
      </div>

      <div className="blogs-content">
        {/* Admin Dashboard */}
        <section className="admin-dashboard" style={{ marginBottom: 28 }}>
          <div className="admin-dashboard-header" style={{ marginBottom: 12 }}>
            <h2 style={{ margin: 0 }}>Admin Dashboard</h2>
            <p style={{ margin: '6px 0 0', color: '#666' }}>
              Manage blog content, publishing, and moderation.
            </p>
          </div>
          <div className="admin-dashboard-stats" style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
            <div className="stat-card" style={{ padding: 12, border: '1px solid #eee', borderRadius: 10, minWidth: 160 }}>
              <div style={{ fontSize: 12, color: '#666' }}>Total Blogs</div>
              <div style={{ fontSize: 22, fontWeight: 600 }}>{blogs.length}</div>
            </div>
            <div className="stat-card" style={{ padding: 12, border: '1px solid #eee', borderRadius: 10, minWidth: 160 }}>
              <div style={{ fontSize: 12, color: '#666' }}>Categories</div>
              <div style={{ fontSize: 22, fontWeight: 600 }}>{totalCategories}</div>
            </div>
            <div className="stat-card" style={{ padding: 12, border: '1px solid #eee', borderRadius: 10, minWidth: 160 }}>
              <div style={{ fontSize: 12, color: '#666' }}>Latest Post</div>
              <div style={{ fontSize: 16, fontWeight: 600 }}>
                {latestBlogDate ? latestBlogDate.toLocaleDateString() : '-'}
              </div>
            </div>
          </div>

          <div className="admin-controls" style={{ marginTop: 18 }}>
            {isAdmin ? (
              <form onSubmit={handleCreateBlog} style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
                <input
                  placeholder="Title"
                  value={adminForm.title}
                  onChange={(e) => setAdminForm({ ...adminForm, title: e.target.value })}
                  required
                />
                <input
                  placeholder="Category"
                  value={adminForm.category}
                  onChange={(e) => setAdminForm({ ...adminForm, category: e.target.value })}
                  required
                />
                <input
                  placeholder="Excerpt"
                  value={adminForm.excerpt}
                  onChange={(e) => setAdminForm({ ...adminForm, excerpt: e.target.value })}
                  required
                />
                <input
                  placeholder="Image URL"
                  value={adminForm.image}
                  onChange={(e) => setAdminForm({ ...adminForm, image: e.target.value })}
                />
                <textarea
                  placeholder="Content"
                  value={adminForm.content}
                  onChange={(e) => setAdminForm({ ...adminForm, content: e.target.value })}
                  rows={4}
                  style={{ gridColumn: '1 / -1' }}
                  required
                />
                <div style={{ display: 'flex', gap: 8 }}>
                  <button type="submit" className="btn btn-primary" disabled={adminSaving}>
                    {adminSaving ? 'Saving...' : 'Create Blog'}
                  </button>
                  <Link to="/dashboard/admin" className="btn btn-secondary">
                    Go to Full Dashboard
                  </Link>
                </div>
              </form>
            ) : (
              <div style={{ color: '#666' }}>
                Admin access required to manage blogs. Log in as an admin to enable dashboard controls.
              </div>
            )}
          </div>
        </section>

        {/* Category Filter */}
        <div className="category-filter">
          {categories.map(cat => (
            <button
              key={cat}
              className={`filter-btn ${category === cat ? 'active' : ''}`}
              onClick={() => setCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* Blogs Grid */}
        {filteredBlogs.length > 0 ? (
          <div className="blogs-grid">
            {filteredBlogs.map(blog => (
              <article key={blog._id} className="blog-card">
                <div className="blog-image">
                  <img src={blog.image} alt={blog.title} />
                  <span className="blog-category">{blog.category}</span>
                </div>
                <div className="blog-content">
                  <h2>{blog.title}</h2>
                  <p className="blog-excerpt">{blog.excerpt}</p>
                  <div className="blog-meta">
                    <span className="blog-author">{blog.author?.name}</span>
                    <span className="blog-date">
                      {new Date(blog.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  {isAdmin && (
                    <div style={{ marginBottom: 10 }}>
                      <button className="btn ghost" onClick={() => handleDeleteBlog(blog._id)}>
                        Delete
                      </button>
                    </div>
                  )}
                  <Link to={`/blogs/${blog._id}`} className="btn btn-secondary">
                    Read More ->
                  </Link>
                </div>
              </article>
            ))}
          </div>
        ) : (
          <div className="no-blogs">
            <p>No blogs found in this category.</p>
          </div>
        )}
      </div>
    </div>
  );
}
