import React, { useEffect, useMemo, useState } from 'react';
import Modal from './Modal';
import BookingForm from './BookingForm';
import ImageCarousel from './ImageCarousel';
import { fetchWithAuth } from '../api';

export default function HotelList() {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState('');
  const [sort, setSort] = useState('recommended');
  const [modalHotel, setModalHotel] = useState(null);
  const [filters, setFilters] = useState({
    minPrice: '',
    maxPrice: '',
    rating: '0',
    amenities: []
  });
  const [searchForm, setSearchForm] = useState({
    destination: '',
    checkIn: '',
    checkOut: '',
    guests: 2
  });

  async function load() {
    setLoading(true);
    try {
      const res = await fetchWithAuth('/api/hotels');
      const data = await res.json();
      setHotels(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
      window.dispatchEvent(new CustomEvent('toast', { detail: { message: 'Failed to load hotels', type: 'error' } }));
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); }, []);

  const amenityOptions = useMemo(() => {
    const set = new Set();
    hotels.forEach(h => (h.amenities || []).forEach(a => set.add(a)));
    return Array.from(set).sort();
  }, [hotels]);

  const filtered = hotels
    .filter(h => {
      const text = `${h.name} ${h.city || ''} ${h.address || ''}`.toLowerCase();
      const q = (query || searchForm.destination || '').toLowerCase();
      if (q && !text.includes(q)) return false;
      if (filters.minPrice && h.price < Number(filters.minPrice)) return false;
      if (filters.maxPrice && h.price > Number(filters.maxPrice)) return false;
      if (filters.rating && h.rating < Number(filters.rating)) return false;
      if (filters.amenities.length) {
        const hotelAmenities = new Set(h.amenities || []);
        const hasAll = filters.amenities.every(a => hotelAmenities.has(a));
        if (!hasAll) return false;
      }
      return true;
    })
    .sort((a,b) => {
      if (sort === 'priceAsc') return a.price - b.price;
      if (sort === 'priceDesc') return b.price - a.price;
      if (sort === 'rating') return b.rating - a.rating;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return new Date(b.createdAt) - new Date(a.createdAt);
    });

  if (loading) return <div className="loading">Loading hotels...</div>;
  if (!hotels.length) return <div className="error">No hotels found.</div>;

  return (
    <div className="hotels-page">
      <div className="hotels-hero">
        <div>
          <h1>Find your next stay</h1>
          <p>Search by destination and compare prices from verified hotel partners.</p>
        </div>
        <div className="hotels-search">
          <div className="hotels-search-grid">
            <div>
              <label>Destination</label>
              <input
                type="text"
                placeholder="City, landmark, or hotel"
                value={searchForm.destination}
                onChange={(e) => setSearchForm({ ...searchForm, destination: e.target.value })}
              />
            </div>
            <div>
              <label>Check-in</label>
              <input
                type="date"
                value={searchForm.checkIn}
                onChange={(e) => setSearchForm({ ...searchForm, checkIn: e.target.value })}
              />
            </div>
            <div>
              <label>Check-out</label>
              <input
                type="date"
                value={searchForm.checkOut}
                onChange={(e) => setSearchForm({ ...searchForm, checkOut: e.target.value })}
              />
            </div>
            <div>
              <label>Guests</label>
              <input
                type="number"
                min="1"
                value={searchForm.guests}
                onChange={(e) => setSearchForm({ ...searchForm, guests: Number(e.target.value) })}
              />
            </div>
          </div>
        </div>
      </div>

      <div className="hotels-layout">
        <aside className="hotels-sidebar">
          <h3>Filters</h3>
          <div className="filter-group">
            <label>Search</label>
            <input
              placeholder="Hotel or city"
              value={query}
              onChange={e => setQuery(e.target.value)}
            />
          </div>
          <div className="filter-group">
            <label>Price range</label>
            <div className="filter-row">
              <input
                type="number"
                placeholder="Min"
                value={filters.minPrice}
                onChange={(e) => setFilters({ ...filters, minPrice: e.target.value })}
              />
              <input
                type="number"
                placeholder="Max"
                value={filters.maxPrice}
                onChange={(e) => setFilters({ ...filters, maxPrice: e.target.value })}
              />
            </div>
          </div>
          <div className="filter-group">
            <label>Minimum rating</label>
            <select
              value={filters.rating}
              onChange={(e) => setFilters({ ...filters, rating: e.target.value })}
            >
              <option value="0">Any</option>
              <option value="3">3.0+</option>
              <option value="4">4.0+</option>
              <option value="4.5">4.5+</option>
            </select>
          </div>
          <div className="filter-group">
            <label>Amenities</label>
            <div className="filter-chips">
              {amenityOptions.map(a => (
                <button
                  key={a}
                  type="button"
                  className={`chip ${filters.amenities.includes(a) ? 'active' : ''}`}
                  onClick={() => {
                    const next = filters.amenities.includes(a)
                      ? filters.amenities.filter(x => x !== a)
                      : [...filters.amenities, a];
                    setFilters({ ...filters, amenities: next });
                  }}
                >
                  {a}
                </button>
              ))}
            </div>
          </div>
          <button className="btn btn-secondary" onClick={() => setFilters({ minPrice: '', maxPrice: '', rating: '0', amenities: [] })}>
            Clear filters
          </button>
        </aside>

        <section className="hotels-results">
          <div className="results-bar">
            <div>
              <strong>{filtered.length}</strong> stays found
            </div>
            <div className="results-actions">
              <select value={sort} onChange={e=>setSort(e.target.value)}>
                <option value="recommended">Recommended</option>
                <option value="rating">Top Rated</option>
                <option value="priceAsc">Price â†‘</option>
                <option value="priceDesc">Price â†“</option>
                <option value="name">Name</option>
              </select>
              <button className="btn" onClick={load}>Refresh</button>
            </div>
          </div>

          <div className="hotel-grid booking-grid">
            {filtered.map(h => (
              <article className="hotel-card grid-card booking-card" key={h._id}>
                <div className="grid-thumb">
                  {h.images && h.images[0] ? (
                    <img
                      src={h.images[0]}
                      alt={`${h.name} thumbnail`}
                      className="hotel-image"
                      onError={(e) => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src = 'data:image/svg+xml;utf8,' +
                          encodeURIComponent('<svg xmlns="http://www.w3.org/2000/svg" width="640" height="360" viewBox="0 0 640 360"><rect width="100%" height="100%" fill="#eef1f6"/><text x="50%" y="50%" dominant-baseline="middle" text-anchor="middle" fill="#9aa4b2" font-family="Arial" font-size="22">Hotel Image</text></svg>');
                      }}
                    />
                  ) : (
                    <div className="hotel-image" />
                  )}
                </div>
                <div className="grid-body">
                  <h3 className="hotel-title">{h.name}</h3>
                  <div className="hotel-meta">{h.address} â€” {h.city}</div>
                  <div className="hotel-tags">
                    <span className="badge success">Free cancellation</span>
                    <span className="badge primary">Pay at property</span>
                  </div>
                  <div className="hotel-meta">Rating: {h.rating} â€¢ {h.amenities?.slice(0, 3).join(', ')}</div>
                </div>
                <div className="grid-actions booking-actions">
                  <div className="price-block">
                    <div className="price">${h.price}</div>
                    <div className="price-note">per night</div>
                  </div>
                  <button className="btn" onClick={() => setModalHotel(h)}>See availability</button>
                </div>
              </article>
            ))}
          </div>
        </section>
      </div>

      {modalHotel && (
        <Modal title={modalHotel.name} onClose={()=>setModalHotel(null)}>
          <div className="hotel-detail">
            {modalHotel.images && modalHotel.images.length ? (
              <div className="hotel-gallery">
                <ImageCarousel images={modalHotel.images} />
              </div>
            ) : null}
            <div className="hotel-detail-content">
              <div className="hotel-detail-main">
                <p className="hotel-location"><strong>Address:</strong> {modalHotel.address} â€” {modalHotel.city}</p>
                <p>{modalHotel.description || 'No description available.'}</p>
                <div className="hotel-meta">Rating: {modalHotel.rating}</div>
                <div className="hotel-amenities">
                  {(modalHotel.amenities || []).map(a => (
                    <span key={a} className="badge">{a}</span>
                  ))}
                </div>
              </div>
              <div className="hotel-detail-booking">
                <div className="price-block large">
                  <div className="price">${modalHotel.price}</div>
                  <div className="price-note">per night</div>
                </div>
                <BookingForm
                  hotelId={modalHotel._id}
                  availableRooms={modalHotel.availableRooms}
                  initialCheckIn={searchForm.checkIn}
                  initialCheckOut={searchForm.checkOut}
                  initialGuests={searchForm.guests}
                  onBooked={()=>{ setModalHotel(null); load(); }}
                />
              </div>
            </div>
          </div>
        </Modal>
      )}
    </div>
  );
}
