import React, { useEffect, useState } from 'react';

export default function AdminPanel(){
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ name: '', address: '', city: '', rating: 4, price: 100 });

  async function load(){
    setLoading(true);
    try{
      const res = await fetch('/api/hotels');
      const data = await res.json();
      setHotels(data);
    }catch(e){ console.error(e); }
    setLoading(false);
  }

  useEffect(()=>{ load(); }, []);

  async function handleCreate(e){
    e.preventDefault();
    try{
      const res = await fetch('/api/hotels', {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(form)
      });
      if(!res.ok) throw await res.json();
      setForm({ name: '', address: '', city: '', rating: 4, price: 100 });
      load();
    }catch(err){ console.error(err); alert(err.message || 'Create failed'); }
  }

  async function handleDelete(id){
    if(!confirm('Delete this hotel?')) return;
    try{
      const res = await fetch(`/api/hotels/${id}`, { method: 'DELETE' });
      if(!res.ok) throw await res.json();
      load();
    }catch(err){ console.error(err); alert(err.message || 'Delete failed'); }
  }

  return (
    <div>
      <h2>Admin Panel</h2>
      <form onSubmit={handleCreate} style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
        <input placeholder="Name" value={form.name} onChange={e=>setForm({...form, name: e.target.value})} required />
        <input placeholder="City" value={form.city} onChange={e=>setForm({...form, city: e.target.value})} required />
        <input placeholder="Address" value={form.address} onChange={e=>setForm({...form, address: e.target.value})} />
        <input type="number" placeholder="Price" value={form.price} onChange={e=>setForm({...form, price: Number(e.target.value)})} style={{ width: 90 }} />
        <input type="number" placeholder="Rating" value={form.rating} step="0.1" onChange={e=>setForm({...form, rating: Number(e.target.value)})} style={{ width: 90 }} />
        <button type="submit">Add</button>
      </form>

      {loading ? <div>Loading...</div> : (
        <ul>
          {hotels.map(h=> (
            <li key={h._id} style={{ marginBottom: 8 }}>
              <strong>{h.name}</strong> â€” {h.city}
              <button onClick={()=>handleDelete(h._id)} style={{ marginLeft: 12 }}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
