import React, { useEffect, useState } from 'react';

export default function HotelList() {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState('');
  const [sort, setSort] = useState('newest');
  const [selected, setSelected] = useState(null);

  async function load() {
    setLoading(true);
    try {
      const res = await fetch('/api/hotels');
      const data = await res.json();
      setHotels(data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); }, []);

  const filtered = hotels
    .filter(h => (h.name + ' ' + h.city).toLowerCase().includes(query.toLowerCase()))
    .sort((a,b) => {
      if (sort === 'priceAsc') return a.price - b.price;
      if (sort === 'priceDesc') return b.price - a.price;
      if (sort === 'rating') return b.rating - a.rating;
      return new Date(b.createdAt) - new Date(a.createdAt);
    });

  if (loading) return <div>Loading hotels...</div>;
  if (!hotels.length) return <div>No hotels found.</div>;

  return (
    <div>
      <div style={{ display: 'flex', gap: 8, marginBottom: 12, alignItems: 'center' }}>
        <input
          placeholder="Search by name or city"
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{ flex: 1, padding: 8 }}
        />
        <select value={sort} onChange={e=>setSort(e.target.value)} style={{ padding: 8 }}>
          <option value="newest">Newest</option>
          <option value="priceAsc">Price ↑</option>
          <option value="priceDesc">Price ↓</option>
          <option value="rating">Rating</option>
        </select>
        <button onClick={load} style={{ padding: '8px 12px' }}>Refresh</button>
      </div>

      {filtered.map(h => (
        <div key={h._id} style={{ border: '1px solid #ddd', padding: 12, marginBottom: 8 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <h3 style={{ margin: 0 }}>{h.name}</h3>
            <button onClick={() => setSelected(selected && selected._id === h._id ? null : h)} style={{ padding: '6px 10px' }}>
              {selected && selected._id === h._id ? 'Hide' : 'Details'}
            </button>
          </div>
          <div>{h.address} — {h.city}</div>
          <div>Rating: {h.rating} • Price: ${h.price}</div>

          {selected && selected._id === h._id && (
            <div style={{ marginTop: 12, background: '#fafafa', padding: 12 }}>
              <div><strong>Description:</strong> {h.description || 'No description available.'}</div>
              <div style={{ marginTop: 8 }}>
                <strong>Actions:</strong>
                <div style={{ marginTop: 8 }}>
                  <button onClick={() => alert('Booking requires login; please sign in.')} style={{ padding: '8px 12px', marginRight: 8 }}>Book</button>
                  <button onClick={async ()=>{
                    if(!confirm('Delete this hotel?')) return;
                    try{
                      const res = await fetch(`/api/hotels/${h._id}`, { method: 'DELETE' });
                      if(res.ok){ load(); setSelected(null); }
                      else { const err = await res.json(); alert(err.message || 'Delete failed'); }
                    }catch(e){ console.error(e); alert('Error deleting'); }
                  }} style={{ padding: '8px 12px' }}>Delete</button>
                </div>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
