import React, { useState } from 'react';

export default function BookingForm({ hotelId, onBooked }){
  const [form, setForm] = useState({ name:'', email:'', checkIn:'', checkOut:'', guests:1 });
  const [loading, setLoading] = useState(false);

  const token = typeof window !== 'undefined' ? localStorage.getItem('token') : null;

  async function handleSubmit(e){
    e.preventDefault();
    if(!token){
      alert('You must be logged in to book. Use Admin Panel to add a mock token.');
      return;
    }
    setLoading(true);
    try{
      const res = await fetch('/api/bookings', {
        method:'POST', headers: { 'Content-Type':'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ hotelId, ...form })
      });
      if(!res.ok){ const err = await res.json(); throw new Error(err.message || 'Booking failed'); }
      const data = await res.json();
      alert('Booking successful');
      onBooked && onBooked(data);
    }catch(e){ alert(e.message); }
    setLoading(false);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col"><input required placeholder="Your name" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} /></div>
          <div className="col"><input required type="email" placeholder="Email" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} /></div>
        </div>
        <div className="row" style={{ marginTop:8 }}>
          <div className="col"><label>Check-in</label><input required type="date" value={form.checkIn} onChange={e=>setForm({...form, checkIn:e.target.value})} /></div>
          <div className="col"><label>Check-out</label><input required type="date" value={form.checkOut} onChange={e=>setForm({...form, checkOut:e.target.value})} /></div>
        </div>
        <div style={{ marginTop:8 }}>
          <label>Guests</label>
          <input type="number" min="1" value={form.guests} onChange={e=>setForm({...form, guests:Number(e.target.value)})} style={{ width:80, marginLeft:8 }} />
        </div>
        <div className="form-actions">
          <button className="btn" type="submit" disabled={loading}>{loading ? 'Booking...' : 'Book Now'}</button>
        </div>
      </form>
    </div>
  );
}
