import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { fetchWithAuth } from '../api';
import decodeJwt from '../utils/decodeJwt';

export default function Blogs() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState('All');
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminSaving, setAdminSaving] = useState(false);
  const [adminForm, setAdminForm] = useState({
    title: '',
    excerpt: '',
    category: 'General',
    image: '',
    content: ''
  });

  useEffect(() => {
    fetchBlogs();
  }, []);

  useEffect(() => {
    const t = typeof window !== 'undefined' ? localStorage.getItem('token') : null;
    if (t) {
      const p = decodeJwt(t);
      setIsAdmin(!!(p && (p.role === 'admin' || p.role === 'super_admin')));
    }
  }, []);

  const fetchBlogs = async () => {
    try {
      const res = await fetch('/api/blogs');
      const data = await res.json();
      setBlogs(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error(err);
      window.dispatchEvent(new CustomEvent('toast', { 
        detail: { message: 'Failed to load blogs', type: 'error' } 
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleCreateBlog = async (e) => {
    e.preventDefault();
    setAdminSaving(true);
    try {
      const res = await fetchWithAuth('/api/blogs', {
        method: 'POST',
        body: JSON.stringify(adminForm)
      });
      const body = await res.json();
      if (!res.ok) throw body;
      setAdminForm({
        title: '',
        excerpt: '',
        category: 'General',
        image: '',
        content: ''
      });
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: 'Blog created', type: 'success' }
      }));
      fetchBlogs();
    } catch (err) {
      console.error(err);
      const msg = (err && err.message) || 'Create failed';
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: msg, type: 'error' }
      }));
    } finally {
      setAdminSaving(false);
    }
  };

  const handleDeleteBlog = async (id) => {
    if (!confirm('Delete this blog?')) return;
    try {
      const res = await fetchWithAuth(`/api/blogs/${id}`, { method: 'DELETE' });
      const body = await res.json();
      if (!res.ok) throw body;
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: 'Blog deleted', type: 'success' }
      }));
      fetchBlogs();
    } catch (err) {
      console.error(err);
      const msg = (err && err.message) || 'Delete failed';
      window.dispatchEvent(new CustomEvent('toast', {
        detail: { message: msg, type: 'error' }
      }));
    }
  };

  const categories = ['All', ...new Set(blogs.map(b => b.category))];
  const filteredBlogs = category === 'All' ? blogs : blogs.filter(b => b.category === category);
  const totalCategories = new Set(blogs.map(b => b.category)).size;
  const latestBlogDate = blogs.reduce((latest, b) => {
    const d = b?.createdAt ? new Date(b.createdAt) : null;
    if (!d || Number.isNaN(d.getTime())) return latest;
    return latest && latest > d ? latest : d;
  }, null);

  if (loading) return <div className="loading">Loading blogs...</div>;

  return (
    <div className="blogs-page">
      <div className="page-header">
        <h1>Travel Blogs</h1>
        <p>Tips, stories, and inspiration for your next journey</p>
      </div>

      <div className="blogs-content">
        {/* Category Filter */}
        <div className="category-filter">
          {categories.map(cat => (
            <button
              key={cat}
              className={`filter-btn ${category === cat ? 'active' : ''}`}
              onClick={() => setCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* Blogs Grid */}
        {filteredBlogs.length > 0 ? (
          <div className="blogs-grid">
            {filteredBlogs.map(blog => (
              <article key={blog._id} className="blog-card">
                <div className="blog-image">
                  <img src={blog.image} alt={blog.title} />
                  <span className="blog-category">{blog.category}</span>
                </div>
                <div className="blog-content">
                  <h2>{blog.title}</h2>
                  <p className="blog-excerpt">{blog.excerpt}</p>
                  <div className="blog-meta">
                    <span className="blog-author">{blog.author?.name}</span>
                    <span className="blog-date">
                      {new Date(blog.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  <Link to={`/blogs/${blog._id}`} className="btn btn-secondary">
                    Read More â†’
                  </Link>
                </div>
              </article>
            ))}
          </div>
        ) : (
          <div className="no-blogs">
            <p>No blogs found in this category.</p>
          </div>
        )}
      </div>
    </div>
  );
}
