import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import decodeJwt from '../utils/decodeJwt';

export default function Navbar({ onSignInClick }) {
  const [user, setUser] = useState(null);
  const [showMenu, setShowMenu] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const syncFromToken = () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const decoded = decodeJwt(token);
          setUser(decoded);
          return;
        } catch (err) {
          console.error('Failed to decode token:', err);
        }
      }
      setUser(null);
    };

    syncFromToken();

    const handleLogout = () => {
      setUser(null);
      setShowMenu(false);
    };

    const handleLogin = () => {
      syncFromToken();
    };

    const handleStorage = (e) => {
      if (e.key === 'token') syncFromToken();
    };

    window.addEventListener('auth-logout', handleLogout);
    window.addEventListener('auth-login', handleLogin);
    window.addEventListener('storage', handleStorage);
    return () => {
      window.removeEventListener('auth-logout', handleLogout);
      window.removeEventListener('auth-login', handleLogin);
      window.removeEventListener('storage', handleStorage);
    };
  }, []);

  const handleLogOut = () => {
    localStorage.removeItem('token');
    setUser(null);
    setShowMenu(false);
    window.dispatchEvent(new CustomEvent('auth-logout'));
    window.dispatchEvent(new CustomEvent('toast', { detail: { message: 'Logged out successfully', type: 'success' } }));
    navigate('/');
  };

  const getDashboardPath = () => {
    if (!user) return '/';
    if (user.role === 'super_admin') return '/dashboard/admin';
    if (['manager', 'receptionist', 'waiter', 'cook'].includes(user.role)) return '/dashboard/employee';
    return '/dashboard/user';
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">
          <h1 className="navbar-title">MERN Hotel</h1>
          <p className="navbar-subtitle">Book Your Perfect Stay</p>
        </Link>

        <button
          className="mobile-menu-toggle"
          onClick={() => setShowMobileMenu(!showMobileMenu)}
        >
          Menu
        </button>

        <div className={`navbar-menu ${showMobileMenu ? 'active' : ''}`}>
          <div className="navbar-links">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/hotels" className="nav-link">Hotels</Link>
            <Link to="/about" className="nav-link">About</Link>
            <Link to="/services" className="nav-link">Services</Link>
            <Link to="/blogs" className="nav-link">Blog</Link>
            <Link to="/contact" className="nav-link">Contact</Link>
          </div>
        </div>

        <div className="navbar-actions">
          {user ? (
            <div className="user-menu">
              <button className="btn user-btn" onClick={() => setShowMenu(!showMenu)} type="button">
                {user.email}
              </button>
              {showMenu && (
                <div className="dropdown-menu" onMouseDown={(e) => e.stopPropagation()}>
                  <div className="dropdown-item disabled">{user.email}</div>
                  <button
                    type="button"
                    className="dropdown-item"
                    onMouseDown={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      setShowMenu(false);
                      navigate(getDashboardPath());
                    }}
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      setShowMenu(false);
                      navigate(getDashboardPath());
                    }}
                  >
                    My Dashboard
                  </button>
                  <button
                    type="button"
                    className="dropdown-item"
                    onMouseDown={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      handleLogOut();
                    }}
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      handleLogOut();
                    }}
                  >
                    Log out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <button className="btn btn-primary" onClick={onSignInClick}>
              Sign In / Register
            </button>
          )}
        </div>
      </div>
    </nav>
  );
}
